package org.pvhees.advent.day2;

import org.junit.Test;

import static org.hamcrest.CoreMatchers.is;
import static org.hamcrest.MatcherAssert.assertThat;

public class KeypadTest {

    private String[] example = {"ULL", "RRDDD", "LURDL", "UUUUD"};

    private String[] opgave = {
            "",
            "",
            "",
            "",
            ""};

    @Test
    public void exampleKeypad1() {
        Keypad1 keypad = new Keypad1();
        String combination = keypad.findCombinationKeypad(example);
        assertThat(combination, is("1985"));
    }

    @Test
    public void opgaveKeypad1() {
        Keypad1 keypad = new Keypad1();
        String combination = keypad.findCombinationKeypad(opgave);
        System.out.println("Combinatie voor deel 1 is " + combination);
    }

    @Test
    public void exampleKeypad2() {
        Keypad2 keypad = new Keypad2();
        String combination = keypad.findCombinationKeypad(example);
        assertThat(combination, is("5DB3"));
    }

    @Test
    public void opgaveKeypad2() {
        Keypad2 keypad = new Keypad2();
        String combination = keypad.findCombinationKeypad(opgave);
        System.out.println("Combinatie voor deel 2 is " + combination);
    }

}
